import React, { Component } from 'react';
import ScrapPostService from '../services/ScrapPostService';

class ListScrapPostComponent extends Component {
    constructor(props){
        super(props)

        this.state = {
            scrapost: []
        }
    }

    componentDidMount(){
        ScrapPostService.getScrapPost.then((res) => {
            this.setState({scrappost: res.data});
        });
    }
    
    render() {
        return (
            <div>
             <h2 className="text-center">Scrap Posts</h2> 
             <div className="row">
             <table className="table table-striped table-bordered">
                 <thead>
                     <tr>
                         <th>city</th>
                         <th>weight</th>
                         <th>Material Type</th>
                         <th>Uploading Date</th>
                         <th>Scrap Image</th>
                     </tr>
                 </thead>

                 <tbody>
                     {
                         this.state.post.map(
                             scrappost =>
                             <tr key={scrappost.id}>
                                 <td>{scrappost.city}</td>
                                 <td>{scrappost.weight}</td>
                                 <td>{scrappost.materialType}</td>
                                 <td>{scrappost.uploadingDate}</td>
                                 <td>{scrappost.scrapImage}</td>
                             </tr>
                         )
                     }
                 </tbody>
                 </table>    
             </div>  
            </div>
        );
    }
}

export default ListScrapPostComponent;
