import React, { Component } from 'react';

class AddUserComponent extends Component {

    constructor(props){
        super(props)

        this.state = {
            fullname:'',
            email:'',
            contactNo:'',
            address:'',
            username:'',
            password:''
        }

        this.changeFullNameHandler = this.changeFullNameHandler.bind(this);
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.changeContactNoHandler = this.changeContactNoHandler.bind(this);
        this.changeAddressHandler = this.changeAddressHandler.bind(this);
        this.changeUsernameHandler = this.changeUsernameHandler.bind(this);
        this.changePasswordHandler = this.changePasswordHandler.bind(this);
        //this.cancelScrapPost = this.cancelScrapPost.bind(this);

    }

    changeFullNameHandler =(event) => {this.setState(
      {fullname:event.target.value} 
    )};

    changeEmailHandler =(event) => {this.setState(
        {email:event.target.value} 
      )};

      changeContactNoHandler =(event) => {this.setState(
        {contactNo:event.target.value} 
      )};

      changeAddressHandler =(event) => {this.setState(
        {address:event.target.value} 
      )};

      changeUsernameHandler =(event) => {this.setState(
        {username:event.target.value} 
      )};

      changePasswordHandler =(event) => {this.setState(
        {password:event.target.value} 
      )};

      addUser = (e) => {e.preventDefault();
      let user = {fullname: this.state.fullname,
                      email: this.state.email,
                      contactNo: this.state.contactNo,
                      address: this.state.address,
                      username: this.state.username,
                      password: this.state.password
                    };
                      console.log('scrap post info='+JSON.stringify(scrappost));

                      ScrapPostService.addScrapPost(scrappost).then(res =>{
                        this.props.history.push('/viewScrapPost');
                      });
      }

      cancelReg(){
        this.props.history.push('/viewScrapPost');
      }


    render() {
        return (
            <div>
                <h2>Registration</h2>
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            <div className="card-body">
                                <form>
                                   <div className="form-group">
                                       <label>Full Name</label>
                                       <input placeholder="Full Name" name="fullname" className="form-control" value={this.state.fullname} onChange={this.changeFullNameHandler}/>
                                       <label>Email </label>
                                       <input placeholder="Email" name="email" className="form-control" value={this.state.email} onChange={this.changeEmailHandler}/>
                                       <label>Contact No.</label>
                                       <input placeholder="Contact No" name="contactNo" className="form-control" value={this.state.contactNo} onChange={this.changeContactNoHandler}/>
                                       <label>Address</label>
                                       <input placeholder="Address" name="address" className="form-control" value={this.state.address} onChange={this.changeAddressHandler}/>
                                       <label>Username</label>
                                       <input placeholder="Username" name="username" className="form-control" value={this.state.username} onChange={this.changeUsernameHandler}/>
                                       <label>Password</label>
                                       <input placeholder="Password" name="password" className="form-control" value={this.state.password} onChange={this.changePasswordHandler}/>
                                       <button className="btn btn-success" onClick={this.addUser}>Register</button>
                                      <button className="btn btn-danger" onClick={this.cancelReg} style={{marginLeft : "10px"}}>Cancel</button>
                                       
                                       </div> 
                                </form>
                            </div>

                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default AddUserComponent;
